invoice.scss:
body {
    background: #fff;
    color: #000;
    margin: 10px;
}

body,
td,
th {
    font-family: Arial, Sans-Serif;
    font-size: 12px;
}

h1,
h1 a {
    color: #000;
    text-decoration: none;
}

.Invoice,
.PackingSlip {
    border: 2px solid #cacaca;
    padding: 5px;
}

.InvoiceTitle,
.PackingSlipTitle {
    background: #000;
    color: #fff;
    font-size: 15px;
    font-weight: bold;
    margin-bottom: 10px;
    padding: 5px;
}

.StoreAddress {
    font-weight: bold;
    margin-bottom: 10px;
}

.InvoiceHeading,
.PackingSlipHeading {
    font-size: 15px;
    font-weight: bold;
    margin-bottom: 10px;
}

.CustomerDetails,
.AddressRow,
.InvoiceItems,
.InvoiceDetails,
.PackingSlipItems,
.PackingSlipDetails {
    margin-bottom: 10px;

    li {
        list-style-type: none;
    }
}

.InvoiceTable,
.PackingSlipTable {
    border-collapse: collapse;
    width: 100%;
}

.InvoiceTable th,
.PackingSlipTable th {
    font-weight: bold;
    padding: 5px;
    text-align: left;
}


.InvoiceTable td,
.PackingSlipTable td {
    padding: 5px;
    text-align: left;
    vertical-align: top;
}

td.ProductQuantity,
th.ProductQuantity,
td.ProductCost,
th.ProductCost,
td.ProductTotal,
th.ProductTotal,
.InvoiceTotals td,
.InvoiceTotalRow td {
    text-align: right;
}

.InvoiceTotalRow:last-child {
    font-weight: bold;
}

.AddressRow,
.InvoiceDetails,
.PackingSlipDetails {
    overflow: hidden;
    width: 100%;
}

.ShippingAddress,
.BillingAddress,
.InvoiceDetailsLeft,
.InvoiceDetailsRight,
.PackingSlipDetailsLeft,
.PackingSlipDetailsRight {
    float: left;
    width: 48%;

    ul {
        padding-left: 0;
    }
}

.InvoiceItems,
.PackingSlipItems {
    border-top: 1px solid #000;
    padding-top: 10px;
}

.InvoiceItemList {
    border-bottom: 1px solid #000;
}

.InvoiceItemDivider td {
    border-top: 1px solid #000;
    padding-top: 10px;
}

.DetailRow,
.ConfigurableProductRow {
    clear: left;
    margin-top: 6px;
    padding-left: 140px;
}

.DetailRow .Label,
.ConfigurableProductRow .Label {
    display: inline;
    float: left;
    margin: 0 0 6px -140px;
    padding-top: 1px;
    position: relative;
    width: 130px;

}

.DetailRow .Value,
.ConfigurableProductRow .Value {
    display: inline;
}

.InvoiceDetails .DetailRow .Label,
.PackingSlipDetails .DetailRow .Label {
    font-weight: bold;
}

.ConfigurableProductRow {
    font-size: 11px;
    margin-left: 10px;
}

.InvoiceTotals .InvoiceTotal td {
    font-weight: bold;
}

.ProductQuantity {
    width: 50px;
}

.ProductCost,
.ProductTotal {
    width: 150px;
}

.PageBreak {
    page-break-after: always;
}

.ProductPreOrder {
    font-size: 11px;
}

assets/scss/components/stencil/productVi:
// =============================================================================
// PRODUCT VIEW (CSS)
// =============================================================================


// Container
// -----------------------------------------------------------------------------

.productView {
    padding-top: spacing("base");
}


// Images
// -----------------------------------------------------------------------------

.productView-images {
    margin-bottom: spacing("double");
}

.productView-image {
    align-items: center;
    display: flex;
    justify-content: center;
    margin: auto;
    position: relative;

    @include breakpoint("medium") {
        min-height: 366px;
        min-width: inherit;
    }

    + .productView-thumbnails {
        margin-top: spacing("half");
    }

    @media (min-width: $screen-xxsmall) and (max-width: $screen-medium) {
        pointer-events: none;
    }
}

.productView-img-container {

    @include lazy-loaded-padding("product_size");
    margin: auto;
    max-width: get-width(stencilString("product_size"));
    position: relative;
    width: 100%;

    img {
        @include lazy-loaded-img;
        /* Object-fit polyfill */
        font-family: "object-fit: contain;";
        height: 100%;
        object-fit: contain;
        width: 100%;
    }
}

.productView-thumbnails {
    @include u-listBullets("none");
    @include grid-row($behavior: "nest");

    margin-left: -(spacing("quarter"));
    margin-right: -(spacing("quarter"));

    &[data-slick] {
        opacity: 0;

        &.slick-initialized {
            opacity: 1;
        }
    }

    .slick-list {
        margin-left: remCalc(40px);
        margin-right: remCalc(40px);
    }

    .slick-next {
        right: 0;
    }

    .slick-prev {
        left: 0;
    }

    img {
        @include lazy-loaded-img;
        /* Object-fit polyfill */
        font-family: "object-fit: contain;";
        object-fit: contain;
        position: relative;
        width: get-width(stencilString("productview_thumb_size"));
    }
}

.productView-thumbnail {
    @include grid-column(3);
    padding: spacing("quarter");
    text-align: center;

    @include breakpoint("large") {
        width: 20%;
    }
}

.productView-thumbnail-link {
    border: container("border");
    box-sizing: content-box;
    display: flex;
    height: 67px;
    justify-content: center;
    max-width: 75px;
    position: relative;
    width: 100%;

    &:hover,
    &.is-active {
        border-color: color("greys", "darker");
    }
}


// Details
// -----------------------------------------------------------------------------

.productView-details {
    padding-bottom: spacing("single") + spacing("third");
}


// Details - Product details
// -----------------------------------------------------------------------------

.productView-product {
    @include breakpoint("medium") {
        border-bottom: container("border");
        padding-bottom: spacing("single") + spacing("third");
    }

    > :last-child {
        margin-bottom: 0;
    }
}

.productView-title {
    margin: 0 0 spacing("quarter");
}

.productView-brand {
    margin: 0 1rem spacing("half") 0;

    a {
        &:hover {
            color: $productView-brand-link-color--hover;
        }
    }
}

// Details - Product price
// -----------------------------------------------------------------------------

.productView-price {
    font-size: 21px;
    margin-bottom: spacing("third");

    .price-section--saving {
        font-size: fontSize("smaller");
    }

    abbr {
        border: 0;
        color: inherit;
        font-size: inherit;
    }
}

.productView-reviewLink {
    color: stencilColor("color-textSecondary");
    display: inline-block;
    margin-left: spacing("quarter");
    vertical-align: middle;

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }
    
    &--new {
        padding: 0;
    }
}


// Details - Product Information
// -----------------------------------------------------------------------------

.productView-info {
    @include clearfix;
    margin-top: spacing("half");

    @include breakpoint("small") {
        margin-top: 0;
    }

    > :first-child {

        @include breakpoint("small") {
            margin-top: spacing("single");
        }
    }

    > :last-child {
        margin-bottom: 0;
    }
}

.productView-info-name,
.productView-info-value {
    float: none;
}

.productView-info-name {
    clear: both;
    margin-bottom: spacing("eighth");
    margin-right: spacing("quarter");

    @include breakpoint("small") {
        font-family: fontFamily("headingSans");
        font-size: fontSize("smallest");
        font-weight: fontWeight("normal");
    }
}

.productView-info-value {
    margin-bottom: spacing("quarter");

    @include breakpoint("small") {
        margin-bottom: spacing("base");
    }
}


// Details - Product options
// -----------------------------------------------------------------------------
//
// 1. Align the product options list item radios vertically.
// 2. Resetting font due to inline-block whitespace issue.
// 3. File upload button does not properly display in IE if font-size is 0
//
// -----------------------------------------------------------------------------

.productView-options {
    @include clearfix;
    margin-bottom: spacing("single");
    text-align: center;

    @include breakpoint("small") {
        text-align: left;
    }

    .form-input[type="number"] {
        @include breakpoint("small") {
            width: grid-calc(2, $total-columns);
        }
    }

    .form {
        display: inline;

        .add-to-cart-wrapper {
    display: inline-flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
}

.form-field--increments {
     min-width: 140px;
}

    }

    input[type="file"] {
        font-size: fontSize("smallest"); // 3
    }

    .form-field {
        font-size: 0; // 2
        margin-bottom: 2rem;

        &--error > .form-inlineMessage {
            font-size: 1rem;
        }

        > .form-checkbox + .form-label {
            display: inline-block;
            margin-left: spacing("single");
            width: auto;

            @include breakpoint("small") {
                margin-left: 0;
                width: 100%;
            }
        }

        > .form-radio + .form-label {
            display: inline-block;
            text-align: justify;
        }

        > .form-label:nth-of-type(2) {
            margin-left: 0;
        }
    }

    .form-action {

        @include breakpoint("medium") {
            padding: 0;
            width: auto;
        }

        @include breakpoint("large") {
            display: inline-block;
            margin-top: 1rem;
        }

        .button {
            width: 100%;

            @include breakpoint("medium") {
                width: auto;
            }

            @include breakpoint("large") {
                margin-right: spacing("half");
            }
        }

        .button--primary {
            margin-right: spacing("half");
        }
    }

    .add-to-cart-buttons {
        display: inline-grid;
        float: left;
        margin-bottom: 1rem;
        width: 100%;

        @include breakpoint("small") {
            padding: 0;
            width: 50%;
        }

        @include breakpoint("medium") {
            width: 50%;
        }

        @include breakpoint("large") {
            display: inline-block;
            margin-top: 1rem;
            width: 50%;
        }

        &.pre-order-buttons {
            @include breakpoint("small") {
                min-width: 50%;
                width: auto;
            }

            @include breakpoint("medium") {
                margin-right: remCalc(11px);
                min-width: auto;
                padding: 0;
                width: auto;
            }

            .button {
                padding: remCalc(13px) remCalc(24px);
            }
        }

        .button {
            margin: 0;
            width: 100%;
        }
    }

    .add-to-cart-wallet-buttons {
        margin-top: spacing("half");

        div[data-smart-button-container-id] {
            line-height: 0;
            margin-top: 6px;

            button,
            div {
                width: 100%;
            }
        }

        button.show-more-button {
            color: stencilColor("color-textSecondary");
            display: block;
            margin-top: spacing("quarter");
            padding: spacing("quarter") 0;
            text-align: center;
            text-decoration: underline;
            vertical-align: middle;
            width: 100%;

            &:hover {
                color: stencilColor("color-textSecondary--hover");
            }
        }
    }
}


// Product list (with images)
// -----------------------------------------------------------------------------

.productOptions-list {
    border: $productOptions-list-border;
    list-style: none;
    margin: 0;
    max-height: 400px;
    overflow-y: auto;
    position: relative;
}

.productOptions-list-item {
    padding: $productOptions-list-item-padding;

    + .productOptions-list-item {
        border-top: $productOptions-list-item-borderTop;
    }

    .form-label {
        text-align: left;
        top: -2px; // 1
    }
}

.productOptions-list-item-figure,
.productOptions-list-item-content {
    display: table-cell;
    vertical-align: middle;
}

.productOptions-list-item-figure {
    width: $productOptions-list-item-figure-width;
}

.productOptions-list-item-content {
    padding-left: $productOptions-list-item-content-paddingLeft;
}


// Share products
// -----------------------------------------------------------------------------

.shareProduct {
    text-align: center;
}


// EasyZoom settings for zoomable product image
// -----------------------------------------------------------------------------

.productView-image .easyzoom-flyout {
    height: 100%;
    left: 0;
    overflow: hidden;
    position: absolute;
    top: 0;
    width: 100%;

    img {
        max-width: none;
        width: auto;
    }
}
