maintenance.scss:
@import "tools/tools";

* {
    box-sizing: border-box;
}

body {
    -webkit-align-items: center;
    align-items: center;
    background: #888 url("../img/coming-soon.jpg") no-repeat center center;
    background-size: cover;

    -ms-box-orient: horizontal;
    color: #798289;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    -ms-flex-pack: space-around;
    font-family: Helvetica, Arial, Sans-Serif;
    font-size: 0.8em;
    height: 100vh;

    -webkit-justify-content: space-around;
    justify-content: space-around;
    line-height: 1.5em;
    margin: 0;
    min-height: 480px;
    min-width: 320px;
    padding: 20px 30px;
}

main {
  background-color: #fff;
  box-shadow: 0 2px 0 0 rgba(0, 0, 0, 0.07);
  display: block;
  padding: 20px 30px;
  text-align: center;
}

.header-logo-image-container{
    width: get-width(stencilString('logo_size'));
    margin: auto;
}

.logo{flex: 1 1 100%; margin-bottom: 20px;}
.logo img{max-width: 100%;}

section {
    margin-bottom: 40px;
}

fieldset {
    border: 0;
    padding: 0;
}

legend {
    border: 0;
    clip: rect(0 0 0 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute !important;
    width: 1px;
}

label,
input {
    display: block;
    height: 30px;
    line-height: 30px;
    width: 100%;
}

label {
    color: #313b47;
    font-size: 14px;
    font-weight: bold;
    text-align: center;
}

input {
    -webkit-appearance: none;
    border-radius: 2px;
    border-style: solid;
    width: 100%;
}

input[type=text] {
    background-color: #f7f8f9;
    border: 1px solid #e1e6eb;
    margin-bottom: 1rem;
    padding: 0 1em;
}

input[type=submit] {
    background: #00abc9;
    border: 0;
    color: #fff;
    cursor: pointer;
    font-size: 12px;
    padding: 0 15px;
}

a {
    color: #00b9d3;
    margin: 0 5px;
    text-decoration: none;
}

h1 {
    color: #3c4043;
    font-size: 24px;
    font-weight: 300;
    margin-top: 15px;
}

div p {
    margin-bottom: 0;
    margin-top: 0;
}

.form-field--error {
    margin-bottom: 1rem;
}

.form-field--error input[type=text] {
    margin-bottom: 0;
}

.form-field--error .form-inlineMessage {
    color: #ac0005;
    margin-top: 0.375rem;
    width: 100%;
}

.powered-by {
    color: #acb3be;
    font-size: 11px;
    font-style: italic;
    line-height: 24px;
}

.powered-by a {
    background: url("../img/bc_logo_sprite.png") 0 -75px no-repeat;
    display: inline-block;
    height: 22px;
    opacity: 0.7;
    text-indent: -9999px;
    width: 95px;
}

.powered-by a:hover {
    opacity: 1;
}

@media screen and (min-width: 540px) {
    body {
        font-size: 1em;
    }

    main {
        margin: 0 auto;
        padding: 45px 60px;
        width: 500px;
    }

    label,
    input {
        display: inline-block;
        margin-right: 10px;
        width: auto;
    }

    label {
        text-align: left;
    }

    input[type=text] {
        margin-bottom: 0;
        margin-right: 10px;
    }

    .form-field {
        display: inline-block;
        position: relative;
    }

    .form-field--error .form-inlineMessage {
        left: 100px;
        position: absolute;
        top: 30px;
    }
}

